jQuery(document).ready(function(){document.body;const e=document.querySelector(".main-header"),t="scroll-up",o="scroll-down";let n=0;document.addEventListener("scroll",()=>{var s=window.pageYOffset;s<=0?e.classList.remove(t):(s>n&&!e.classList.contains(o)?(e.classList.remove(t),e.classList.add(o)):(s<n&&e.classList.contains(o)||window.innerHeight+window.pageYOffset>=document.body.offsetHeight)&&(e.classList.remove(o),e.classList.add(t)),n=s)})});